import logo from './logo.svg';
import './App.css';
import Header from "./components/Header"
import LoginPage from "./components/LoginPage"
import ShopPage from "./components/ShopPage"
import SellPage from "./components/SellPage"
import Cart from "./components/Cart"
import { useEffect, useState } from 'react';
import { getByDisplayValue } from '@testing-library/react';

function App() {

  const [ itemsArray, setItemsArray ] = useState([]);
  const [ searchTerm, setSearchTerm ] = useState("");
  const [ displayedItems, setDisplayedItems ] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/items")
    .then(resp => resp.json())
    .then(itemsData => {
      // console.log(itemsData);
      setItemsArray(itemsData);
      setDisplayedItems(itemsData);
    })
  }, [])

  function handleSearchSubmit(term) {
    let displayedItems = itemsArray.filter(item => item.name.toLowerCase().includes(term.toLowerCase()));
    console.log(displayedItems);
  }

  return (
    <div>
      <Header />
      <LoginPage />
      <ShopPage itemsArray={itemsArray} handleSearchSubmit={handleSearchSubmit}/>
      <SellPage />
      <Cart />
    </div>
  );
}

export default App;
