import react, { useState } from "react";
import NewItemForm from "./NewItemForm";

function NewItemCard({ selectUser }){

  const [ showNewForm, setShowNewForm ] = useState(false);

  return(
    <div className="item-card">
      <img src="https://upload.wikimedia.org/wikipedia/commons/9/9c/Golden_star.svg"></img>
      <span></span>
      <h3>Sell New Item</h3>
      <p></p>
      <button onClick={() => setShowNewForm(!showNewForm)}>{buttonText}</button>
      {showNewForm ? <NewItemForm selectUser={selectUser}/> : null}
    </div>
  )
}

export default NewItemCard;
