import React, { useState } from "react";
import { Link } from "react-router-dom"

function LoginPage({ userArr, handleUser }){

  const [ input, setInput ] = useState([])
  let nextLink = "/";

  function handleSend(e){
    if (e.target.value==="no-select") {
      nextLink="/";
      alert("Please select a user");
    } else {
      nextLink = "/shop"
      handleUser(input)
    }
  }

  function handleChange(e){
    console.log(e.target.value);
    if (e.target.value !== "no-select") {
      const selectedUser = userArr.filter((users)=> users.id === parseInt(e.target.value))
      return(
        setInput(selectedUser)
      )}
    }

  return(

    <div id="loginFormBox">
      <h3 id="loginPrompt">WELCOME, PLEASE LOG IN</h3>
      <form id="loginForm" onChange={handleChange}>
        <select >
          <option value="no-select">Select User</option>
          {userArr.map(user => {
            return(
            <option value={user.id} key={user.id} >{user.username}</option>
            )}
          )}
        </select>
      </form>
      <Link to={nextLink}>
        <button id="loginBttn" onClick={handleSend} >Login</button>
      </Link>
    </div>
  )
}

export default LoginPage;
